(2, 'Inclusions', '<ul class="inc_list">\r\n	<li>0 Nights stay on twin sharing basis at</li>\r\n	<li>0 Night stay on twin sharing basis at</li>\r\n	<li>Buffet Breakfast.</li>\r\n	<li>Assistance on arrival and departure at Airport by MyTravelDesk representative</li>\r\n	<li>Local Guides for Sight-seeing as per itinerary.</li>\r\n	<li>All Transportation for local sight-seeing tours and airport/ hotel/ Jetty transfers by AC vehicle as per Itinerary.</li>\r\n	<li>All entrances and tickets to monuments, museums, Cultural Events and activities suggested in the Itinerary.</li>\r\n</ul>\r\n', '2016-02-23 04:42:51', '2016-02-22 23:12:51'),
(3, 'Exclusions', '<ul class="exc_list">\r\n	<li>Airfare &amp; Train Fare</li>\r\n	<li>Entrance Fees, parking &amp; Guide charges</li>\r\n	<li>Any personal expensive &ndash; laundry , shopping, tip etc.</li>\r\n	<li>Meal other than specified</li>\r\n	<li>Monument entrance fee / Camera fees</li>\r\n	<li>Travel Insurance</li>\r\n	<li>Anything which is not include in the inclusion</li>\r\n	<li>3.625 % Govt. Service Tax Extra</li>\r\n</ul>\r\n', '2016-02-23 04:43:12', '2016-02-22 23:13:12'),
(4, 'Cancellation Policy', '<ul class="list">\r\n	<li>From booking date till 21 Days prior to departure : 25% of the Total Package Cost</li>\r\n	<li>From 21 Days to 14 Days prior to departure : 50% of the Total Package Cost</li>\r\n	<li>From 14 Days to 08 Days prior to departure : 100% of the Total Package Cost</li>\r\n	</ul>\r\n	<strong>All booking vouchers and tickets will be provided 3 days before departure.</strong>\r\n\r\n', '2016-02-27 17:53:28', '2016-02-27 12:23:28'),
(5, 'Payment Policy', '<ul class="list">\r\n	<li>50% of the package cost at the time of booking confirmation.</li>\r\n	<li>Balance 50% of the package cost 8 days prior to the travel date.</li>\r\n<br/>\r\n<strong>Important :</strong>The booking stands liable to be cancelled if 100% payment is not received less than 08 days before date of departure.\r\n</ul>', '2016-02-27 17:51:40', '2016-02-27 12:21:40'),
